require 'digest'

class EgFileIo < Formula
  url 'file://'+File.expand_path(__FILE__)
  desc 'Dependencies for the EgFileIo formula'
  sha256 Digest::SHA256.file(File.expand_path(__FILE__)).hexdigest
  version '4'

  depends_on 'fontconfig' => ["without-docs"]
  depends_on 'genometools'
  depends_on 'ensembl/external/maftools'
  depends_on 'ensembl/external/newick-utils'

  def install
    File.open('eg-file-io', 'w') { |file|
      file.write '#!/bin/sh'+"\n"
      deps.each do | dep |
        f = dep.to_formula
        file.write "echo "+[f.full_name, f.version, f.prefix].join("\t")+"\n"
      end
    }
    bin.install 'eg-file-io'
  end
end
    
